// Custom jQuery initialization for CSharpAIAssistant
$(document).ready(function() {
    // Initialize tooltips
    $('[data-bs-toggle="tooltip"]').tooltip();
    
    // Initialize popovers
    $('[data-bs-toggle="popover"]').popover();
    
    // Handle sensitive data visibility toggle
    $('#chkShowSensitive').on('change', function() {
        if ($(this).is(':checked')) {
            $('.sensitive-value').removeClass('d-none');
            $('.sensitive-placeholder').addClass('d-none');
        } else {
            $('.sensitive-value').addClass('d-none');
            $('.sensitive-placeholder').removeClass('d-none');
        }
    });
    
    // Auto-refresh for task status (if on task list page)
    if ($('#task-list-container').length > 0) {
        setInterval(function() {
            // Auto-refresh logic could be implemented here
            // For now, just add a visual indicator
            $('#last-refresh').text('Last updated: ' + new Date().toLocaleTimeString());
        }, 30000); // Refresh every 30 seconds
    }
});
